// screens/FlightScheduleScreen.js
import React, { useState } from 'react';
import { View, Text, TextInput, Button } from 'react-native';
import { lightGreen100 } from 'react-native-paper/lib/typescript/src/styles/themes/v2/colors';

const FlightScheduleScreen = ({ navigation }) => {
  const [departure, setDeparture] = useState('');
  const [destination, setDestination] = useState('');
  const [departureDate, setDepartureDate] = useState('');

  const handleSearch = () => {
    // melakukan pencarian jadwal pesawat berdasarkan input yang diberikan
    // kemudian navigasi ke layar FlightListScreen dengan parameter data jadwal pesawat
    navigation.navigate('FlightList', { departure, destination, departureDate });
  }

  return (
    <View style={{ background: 'blue' ,flex: 1, alignItems: 'center'}}>
      <Text style={{background: 'blue', }}>Asal:</Text>
      <TextInput
        placeholder="Kota keberangkatan"
        value={departure}
        onChangeText={setDeparture}
      />
      <Text>Tujuan:</Text>
      <TextInput
        placeholder="Kota tujuan"
        value={destination}
        onChangeText={setDestination}
      />
      <Text>Tanggal Keberangkatan:</Text>
      <TextInput
        placeholder="Tanggal keberangkatan"
        value={departureDate}
        onChangeText={setDepartureDate}
      />
      <Button
        title="Cari Jadwal Pesawat"
        onPress={handleSearch}
      />
    </View>
  );
}

export default FlightScheduleScreen;
