import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, StyleSheet } from 'react-native';
import DatePicker from 'react-native-datepicker';

const FlightSearch = ({ navigation }) => {
  const [origin, setOrigin] = useState('');
  const [destination, setDestination] = useState('');
  const [date, setDate] = useState('');


  const handleSearch = () => {
    // melakukan pencarian jadwal pesawat berdasarkan input yang diberikan
    // kemudian navigasi ke layar FlightListScreen dengan parameter data jadwal pesawat
    navigation.navigate('FlightList', { origin, destination, date });
  }

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Flight Search</Text>
      <TextInput
        style={styles.input}
        placeholder="Origin"
        onChangeText={setOrigin}
        value={origin}
      />
      <TextInput
        style={styles.input}
        placeholder="Destination"
        onChangeText={setDestination}
        value={destination}
      />

      <Text style={styles.text}>Birth Date :</Text>
      <DatePicker
        style={styles.datePickerStyle}
        date={date}
        mode="date"
        placeholder="select date"
        format="DD/MM/YYYY"
        minDate="01-01-1900"
        maxDate="01-01-2000"
        confirmBtnText="Confirm"
        cancelBtnText="Cancel"
        customStyles={{
          dateIcon: {
            position: 'absolute',
            right: -5,
            top: 4,
            marginLeft: 0,
          },
          dateInput: {
            borderColor: "gray",
            alignItems: "flex-start",
            borderWidth: 0,
            borderBottomWidth: 1,
          },
          placeholderText: {
            fontSize: 17,
            color: "gray"
          },
          dateText: {
            fontSize: 17,
          }
        }}
        onDateChange={(date) => {
          setDate(date);
        }}
      />
      <TouchableOpacity onPress={handleSearch} style={styles.button}>
        <Text style={styles.buttonText}>Search Flights</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    padding: 16,
  },
  title: {
    fontSize: 24,
    marginBottom: 16,
  },
  input: {
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 4,
    paddingHorizontal: 8,
    paddingVertical: 4,
    marginBottom: 16,
  },
  button: {
    backgroundColor: '#007bff',
    borderRadius: 4,
    padding: 8,
  },
  buttonText: {
    color: '#fff',
    textAlign: 'center',
  },
});

export default FlightSearch;
